import "./App.css";
import Product from "./components/product";
import io from "socket.io-client";
import { useEffect } from "react";

//const socket = io.connect("http://" + "192.168.1.5" + ":3001");
const socket = io.connect("http://" + "localhost" + ":3001");

function App() {
  let isRecived = false;
  let totalAmount = 0;
  const products = [
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
  ];

  useEffect(() => {
    socket.on("msg_recived", (data) => {
      if (!isRecived) {
        console.log(data + " dato ricevuto");
        if (data === "true") {
          var modal = document.getElementById("myModal");

          // Get the button that opens the modal
          var btn = document.getElementById("myBtn");

          // Get the <span> element that closes the modal
          var span = document.getElementsByClassName("close")[0];

          // When the user clicks the button, open the modal
          btn.onclick = function () {
            modal.style.display = "block";
          };

          // When the user clicks on <span> (x), close the modal
          span.onclick = function () {
            modal.style.display = "none";
          };

          // When the user clicks anywhere outside of the modal, close it
          window.onclick = function (event) {
            if (event.target == modal) {
              modal.style.display = "none";
            }
          };
        }
      }
    });
  }, [socket]);

  const sendMessage = () => {
    let TableNumber = document.getElementById("inputTable").value;

    if (TableNumber.trim().length > 0) {
      let msg = "Tavolo:" + TableNumber;
      let itemOrdered = 0;
      products.forEach((prd) => {
        if (prd.amount > 0) {
          itemOrdered++;
          msg += ";" + prd.name + ":" + prd.amount;
        }
      });
      if (itemOrdered > 0) {
        socket.emit("sendData", "Ordine:" + msg);
      } else {
        alert("DEVI ORDINARE ALMENO UN PRODOTTO");
      }
    } else {
      alert("DEVI INSERIRE IL NUMERO DEL TAVOLO");
    }
  };

  const resetUI = () => {
    isRecived = false;
    let i = 0;
    products.forEach((element) => {
      element.amount = 0;
      document.getElementById(i).innerHTML =
        element.name +
        " ( " +
        element.price +
        "€ ) x " +
        element.amount +
        "<br/> <span>" +
        element.description +
        "</span>";
      i++;
    });
  };

  return (
    <div className="App">
      <div className="header">
        <input type="number" id="inputTable" placeholder="Numero Tavolo" />
        <button onClick={sendMessage}>Send message</button>
      </div>
      <div className="main">
        <h1>Prodotti</h1>
        <ul>
          {products.map((item, index) => (
            <li key={index}>
              <div className="product">
                <button
                  className="btnRemove"
                  onClick={() => {
                    const precCount = item.amount;
                    if (item.amount > 0) {
                      item.amount = item.amount - 1;
                    }
                    document.getElementById(index).innerHTML =
                      item.name +
                      " ( " +
                      item.price +
                      "€ ) x " +
                      item.amount +
                      "<br/> <span>" +
                      item.description +
                      "</span>";
                    if (precCount > 0) {
                      totalAmount -= item.price;
                      document.getElementById("totalAmount").innerHTML =
                        totalAmount;
                    }
                  }}
                >
                  -
                </button>
                <div className="lblProduct" id={index}>
                  {item.name} ( {item.price}$ ) x {item.amount} <br />{" "}
                  <span>{item.description}</span>
                </div>
                <button
                  className="btnAdd"
                  onClick={() => {
                    item.amount = item.amount + 1;
                    document.getElementById(index).innerHTML =
                      item.name +
                      " ( " +
                      item.price +
                      "€ ) x " +
                      item.amount +
                      "<br/> <span>" +
                      item.description +
                      "</span>";
                    totalAmount += item.price;
                    document.getElementById("totalAmount").innerHTML =
                      totalAmount;
                  }}
                >
                  {" "}
                  +
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="footer">
        <span>
          Totale:<span id="totalAmount">00.00</span>€
        </span>
      </div>
      <button id="myBtn">Open Modal</button>

      <div id="myModal" className="modal">
        <div className="modal-content">
          <span className="close">&times;</span>
          <p>Some text in the Modal..</p>
        </div>
      </div>
    </div>
  );
}

export default App;
