import "./App.css";
import Product from "./components/product";
import io from "socket.io-client";
import { useEffect } from "react";
const socket = io.connect("http://localhost:3001");

function App() {
  const products = [
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
    new Product("CocaCola", "Prodotta da CocaCola", 3.0, 0),
    new Product("Fanta", "Prodotta da CocaCola", 3.0, 1),
    new Product("Sprite", "Prodotta da CocaCola", 3.0, 2),
    new Product("Acqua", "Prodotta da San Benedetto", 1.5, 3),
  ];

  useEffect(() => {
    socket.on("msg_recived", (data) => {
      alert("L'ordine è arrivato, il totate +")
    });
  }, [socket]);

  const sendMessage = () => {

    let TableNumber = document.getElementById("inputTable").value;

    if (TableNumber.trim().length > 0) {
      let msg = "Tavolo:" + TableNumber;
      let itemOrdered = 0;
      products.forEach((prd) => {
        if (prd.amount > 0) {
          itemOrdered++;
          msg += ";" + prd.name + ":" + prd.amount;
        }
      });
      if (itemOrdered > 0) {
        socket.emit("sendData", "Ordine:" + msg);
      } else {
        alert("DEVI ORDINARE ALMENO UN PRODOTTO");
      }
    } else {
      alert("DEVI INSERIRE IL NUMERO DEL TAVOLO");
    }
  };

  return (
    <div className="App">
      <div className="header">
        <input type="number" id="inputTable" placeholder="Numero Tavolo" />
        <button onClick={sendMessage}>Send message</button>
      </div>
      <div className="main">
        <h1>Prodotti</h1>
        <ul>
          {products.map((item, index) => (
            <li key={index}>
              <div className="product">
                <button
                  className="btnRemove"
                  onClick={() => {
                    const precCount = item.count;
                    if (item.amount > 0) {
                      item.amount = item.amount - 1;
                    }
                    document.getElementById(index).innerHTML =
                      item.name +
                      " ( " +
                      item.price +
                      "€ ) x " +
                      item.amount +
                      "<br/> <span>" +
                      item.description +
                      "</span>";
                    if (precCount > 0) {
                      let totalAmount = parseInt(
                        document.getElementById("totalAmount").value
                      );
                      totalAmount -= item.amount;
                      document.getElementById("totalAmount").innerHTML =
                        totalAmount;
                    }
                  }}
                >
                  -
                </button>
                <div className="lblProduct" id={index}>
                  {item.name} ( {item.price}$ ) x {item.amount} <br />{" "}
                  <span>{item.description}</span>
                </div>
                <button
                  className="btnAdd"
                  onClick={() => {
                    item.amount = item.amount + 1;
                    document.getElementById(index).innerHTML =
                      item.name +
                      " ( " +
                      item.price +
                      "€ ) x " +
                      item.amount +
                      "<br/> <span>" +
                      item.description +
                      "</span>";
                    let totalAmount = parseInt(
                      document.getElementById("totalAmount").innerHTML
                    );
                    totalAmount += item.amount;
                    document.getElementById("totalAmount").innerHTML =
                      totalAmount;
                  }}
                >
                  +
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="footer">
        <span>
          Totale:<span id="totalAmount">00.00</span>€
        </span>
      </div>
      <div className="confirmMask">
        <div className="close">x</div>
        <div className="Title">Ordine confermato procedi al pagamento</div>
        <div className="description">il totale è di 0,00€</div>
      </div>
    </div>
  );
}

export default App;
